
package test;

public class DatatypeTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //int
		int i = 10;
        System.out.println(i);
        i = 20;
        System.out.println(i);
        i= i+10;
        System.out.println(i);
        i = i*2;
        System.out.println(i);
        System.out.println(i*2);
        System.out.println(i);
        
        
        System.out.println(i++);
        System.out.println(i);
        
        //double
        double d = 10.20;
        System.out.println(d);
        d = d*2;
        System.out.println(d);
        
        //Char
		char a = 'b';
		System.out.println(a);
		a = '8';
		System.out.println(a);
		
		//boolean
		boolean x = true;
		System.out.println(x);
		x= false;
		System.out.println(x);
		
	}

}
